import { useEffect, useState } from "react";
import { Outlet } from "react-router";
import Datepick from "./Components/Datepick";
import { backdateByDays } from "./libs/backdateByDays";
import "./App.css";
import "react-datepicker/dist/react-datepicker.css";

interface Props {
  copyright: string;
  date: string;
  explanation: string;
  hdurl: string;
  media_type: string;
  service_version: string;
  title: string;
  url: string;
}

function App() {
  const [images, setImages] = useState<Props[]>([]);
  const [image, setImage] = useState<Props>();
  const start_date = backdateByDays(6);
  const end_date = backdateByDays(0);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const date = params.get("date")?.toString();

    let url = new URL("/planetary/apod", process.env.REACT_APP_NASA_BASE_URL);
    url.searchParams.append("api_key", String(process.env.REACT_APP_NASA_API));
    if (!date) {
      url.searchParams.append("start_date", start_date);
      url.searchParams.append("end_date", end_date);
    } else {
      url.searchParams.delete("start_date");
      url.searchParams.delete("end_date");
      url.searchParams.append("date", String(date));
    }

    fetch(url.href)
      .then((response) => response)
      .then((e) => e.json())
      .then((e) => {
        if (!date) {
          return setImages(e);
        }
        return setImage(e);
      });
  }, [end_date]);

  return (
    <div className="App">
      <section className="Main">
        <div className="Main-datepicker">
          <Datepick />
        </div>
        <div className="Main-display">
          <img
            className="Main-image"
            src={image ? image.hdurl : images[5]?.hdurl}
          />
        </div>
      </section>
      <Outlet />
    </div>
  );
}

export default App;
