import { useEffect, useState } from "react";
import dayjs from "dayjs";
import DatePicker from "react-datepicker";
import { Outlet } from "react-router";
import "./App.css";
import "react-datepicker/dist/react-datepicker.css";
import Datepick from "./Components/Datepick";

interface Props {
  copyright: string;
  date: string;
  explanation: string;
  hdurl: string;
  media_type: string;
  service_version: string;
  title: string;
  url: string;
}

const backdateByDays = (number: number) => {
  const currentDate = new Date();
  currentDate.setDate(currentDate.getDate() - number);
  const year = currentDate.getFullYear();
  const month = String(currentDate.getMonth() + 1).padStart(2, "0");
  const day = String(currentDate.getDate()).padStart(2, "0");
  return `${year}-${month}-${day}`;
};

function App() {
  const [images, setImages] = useState<Props[]>([]);
  const start_date = backdateByDays(6);
  const end_date = backdateByDays(0);

  useEffect(() => {
    fetch(
      `https://api.nasa.gov/planetary/apod?api_key=DJnbcPU0nmH4c9DUejNgqxVpFh8wB61Jo8RiHeLc&start_date=${start_date}&end_date=${end_date}`
    )
      .then((response) => response)
      .then((e) => e.json())
      .then((e) => setImages(e));
  }, []);

  return (
    <div className="App">
      <section className="Main">
        <div className="Main-datepicker">
          <Datepick />
        </div>
        <div className="Main-display">
          <img className="Main-image" src={images[5]?.hdurl} />
        </div>
      </section>
      <Outlet />
    </div>
  );
}

export default App;
