import { useState } from "react";
import { useSearchParams } from "react-router-dom";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.min.css";

interface Props {
  copyright: string;
  date: string;
  explanation: string;
  hdurl: string;
  media_type: string;
  service_version: string;
  title: string;
  url: string;
}
const Datepick = () => {
  const [images, setImages] = useState<Props[]>([]);
  const [image, setImage] = useState<Props>();

  const params = new URLSearchParams(window.location.search);

  let url = new URL("/planetary/apod", process.env.REACT_APP_NASA_BASE_URL);
  url.searchParams.append("api_key", String(process.env.REACT_APP_NASA_API));
  url.searchParams.append("date", String(params.get("date")?.toString()));

  console.log("url", url);
  const [searchParams, setSearchParams] = useSearchParams();
  const [startDate, setStartDate] = useState(new Date());

  //date formatting
  const date = new Date(startDate);
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const day = String(date.getDate()).padStart(2, "0");
  const dateFormat = `${year}-${month}-${day}`;

  return (
    <DatePicker
      selected={new Date(startDate)}
      onChange={(date) => {
        if (date !== null) {
          setStartDate(date);
          fetch(url.href)
            .then((response) => response)
            .then((e) => e.json())
            .then((e) => {
              if (!date) {
                return setImages(e);
              }
              return setImage(e);
            });
        }
        setSearchParams({ date: dateFormat });
      }}
    />
  );
};

export default Datepick;
