import { backdateByDays } from "./backdateByDays";

export async function fetchData() {
  const start_date = backdateByDays(6);
  const end_date = backdateByDays(0);

  let url = new URL("/planetary/apod", process.env.REACT_APP_NASA_BASE_URL);
  const params = new URLSearchParams(window.location.search);
  const date = params.get("date")?.toString();
  url.searchParams.append("api_key", String(process.env.REACT_APP_NASA_API));
  if (!date) {
    url.searchParams.append("start_date", start_date);
    url.searchParams.append("end_date", end_date);
  } else {
    url.searchParams.delete("start_date");
    url.searchParams.delete("end_date");
    url.searchParams.append("date", String(date));
  }

  return await fetch(url.href)
    .then((response) => response)
    .then((e) => e.json());
}
