import { useEffect, useState } from "react";
import { Outlet } from "react-router";
import "./App.css";
import "react-datepicker/dist/react-datepicker.css";
import Datepick from "./Components/Datepick";
import { backdateByDays } from "./libs/backdateByDays";

interface Props {
  copyright: string;
  date: string;
  explanation: string;
  hdurl: string;
  media_type: string;
  service_version: string;
  title: string;
  url: string;
}

function App() {
  const [images, setImages] = useState<Props[]>([]);
  const [image, setImage] = useState<string>("");
  const start_date = backdateByDays(6);
  const end_date = backdateByDays(1);

  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const date = params.get("date")?.toString();
    let url = new URL("/planetary/apod", "https://api.nasa.gov");
    url.searchParams.append(
      "api_key",
      "DJnbcPU0nmH4c9DUejNgqxVpFh8wB61Jo8RiHeLc"
    );

    if (!date) {
      url.searchParams.append("start_date", start_date);
      url.searchParams.append("end_date", end_date);
    } else {
      url.searchParams.delete("start_date");
      url.searchParams.delete("end_date");
      url.searchParams.append("date", String(date));
    }

    fetch(url.href)
      .then((response) => response)
      .then((e) => e.json())
      .then((e) => {
        if (!date) {
          setImages(e);
        } else {
          setImage(e);
        }
      });
  }, []);

  return (
    <div className="App">
      <section className="Main">
        <div className="Main-datepicker">
          <Datepick />
        </div>
        <div className="Main-display">
          <img className="Main-image" src={image ? image : images[5]?.hdurl} />
        </div>
      </section>
      <Outlet />
    </div>
  );
}

export default App;
