import { useEffect, useState } from "react";
import dayjs from "dayjs";
import { Outlet } from "react-router";
import "./App.css";

interface Props {
  copyright: string;
  date: string;
  explanation: string;
  hdurl: string;
  media_type: string;
  service_version: string;
  title: string;
  url: string;
}

function App() {
  const date = new Date();
  const year = date.getFullYear();
  const month = String(date.getMonth() + 1).padStart(2, "0");
  const day = String(date.getDate()).padStart(2, "0");
  const formattedDate = `${year}-${month}-${day}`;
  const start_date = formattedDate;
  const end_date = date.setDate(date.getDate() - 7);
  console.log("enddaa", end_date);

  // const currentDate = new Date();
  // currentDate.setDate(currentDate.getDate() - 7);

  const [images, setImages] = useState<Props[]>([]);

  useEffect(() => {
    fetch(
      "https://api.nasa.gov/planetary/apod?api_key=DJnbcPU0nmH4c9DUejNgqxVpFh8wB61Jo8RiHeLc&start_date=2024-08-23&end_date=2024-08-30"
    )
      .then((response) => response)
      .then((e) => e.json())
      .then((e) => setImages(e));
  }, []);

  return (
    <div className="App">
      {images.map((item, index) => (
        <li key={index}>{item.copyright}</li>
      ))}
      <Outlet />
    </div>
  );
}

export default App;
