import { useEffect, useState } from "react";
import dayjs from "dayjs";
import { Outlet } from "react-router";
import "./App.css";

interface Props {
  copyright: string;
  date: string;
  explanation: string;
  hdurl: string;
  media_type: string;
  service_version: string;
  title: string;
  url: string;
}

const backDateByNumOfDays = (number: number) => {
  const currentDate = new Date();
  currentDate.setDate(currentDate.getDate() - number);
  const year = currentDate.getFullYear();
  const month = String(currentDate.getMonth() + 1).padStart(2, "0");
  const day = String(currentDate.getDate()).padStart(2, "0");
  return `${year}-${month}-${day}`;
};

function App() {
  const [images, setImages] = useState<Props[]>([]);
  const start_date = backDateByNumOfDays(7);
  const end_date = backDateByNumOfDays(0);

  // console.log("filing date", start_date, "end date", end_date);

  useEffect(() => {
    fetch(
      `https://api.nasa.gov/planetary/apod?api_key=DJnbcPU0nmH4c9DUejNgqxVpFh8wB61Jo8RiHeLc&start_date=${start_date}&end_date=${end_date}`
    )
      .then((response) => response)
      .then((e) => e.json())
      .then((e) => setImages(e));
  }, []);

  return (
    <div className="App">
      {images.map((item, index) => (
        <li key={index}>{item.copyright}</li>
      ))}
      <Outlet />
    </div>
  );
}

export default App;
